import React, { useState } from "react";
import {
  MapContainer,
  TileLayer,
  Marker,
  useMapEvents
} from "react-leaflet";

import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "leaflet-routing-machine";

function Routing({ pickup, drop }) {

  const map = useMapEvents({});

  if (pickup && drop) {

    L.Routing.control({

      waypoints: [
        L.latLng(pickup[0], pickup[1]),
        L.latLng(drop[0], drop[1])
      ],

      lineOptions: {
        styles: [{ color: "black", weight: 4 }]
      },

      addWaypoints: false,
      draggableWaypoints: false,
      createMarker: () => null

    }).addTo(map);

  }

  return null;
}

function Map() {

  const [pickup, setPickup] = useState(null);
  const [drop, setDrop] = useState(null);

  function ClickHandler() {

    useMapEvents({

      click(e) {

        if (!pickup) {

          setPickup([e.latlng.lat, e.latlng.lng]);

        }
        else if (!drop) {

          setDrop([e.latlng.lat, e.latlng.lng]);

        }

      }

    });

    return null;
  }

  return (

    <MapContainer
      center={[28.6139, 77.2090]}
      zoom={13}
      style={{ height: "500px", width: "100%" }}
    >

      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />

      <ClickHandler/>

      {pickup && <Marker position={pickup}/>}
      {drop && <Marker position={drop}/>}

      <Routing pickup={pickup} drop={drop}/>

    </MapContainer>

  );

}

export default Map;
