import React, { useState } from "react";
import { FaUser, FaPhone, FaCreditCard, FaTimes } from "react-icons/fa";
import axios from 'axios';  // ‚úÖ Add this import

function Book({ fare, vehicle, driver, pickupLocation, dropLocation, distance, duration, onClose, onRideRequested }) {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [paymentMethod, setPaymentMethod] = useState("cash");
  const [loading, setLoading] = useState(false);

  const handleConfirm = async () => {
    if (!name || !phone) {
      alert('Please fill all details');
      return;
    }

    setLoading(true);
    
    try {
      console.log("üöÄ Sending ride request...", {
        driver,
        pickupLocation,
        dropLocation,
        fare
      });

      // Send ride request to backend
      const res = await axios.post('http://localhost:5000/api/rides/request', {
        userId: 'user_' + Date.now(), // Temporary user ID
        driverId: driver?._id,
        pickupLocation: {
          lat: pickupLocation?.lat,
          lng: pickupLocation?.lng,
          address: pickupLocation?.address || 'Pickup location'
        },
        dropLocation: {
          lat: dropLocation?.lat,
          lng: dropLocation?.lng,
          address: dropLocation?.address || 'Drop location'
        },
        fare: fare,
        distance: distance,
        duration: duration,
        status: 'searching'
      });

      console.log("‚úÖ Ride request response:", res.data);

      // Call parent callback
      if (onRideRequested) {
        onRideRequested(res.data.ride);
      }

      // Show success message
      alert(`üéâ Ride Request Sent!\n\nSearching for drivers near you...`);
      
      onClose(); // Close the booking modal
      
    } catch (error) {
      console.error("‚ùå Error sending ride request:", error);
      alert('Failed to send ride request. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.overlay}>
      <div style={styles.modal}>
        {/* Header */}
        <div style={styles.header}>
          <h2 style={styles.title}>Confirm Your Ride</h2>
          <FaTimes style={styles.closeIcon} onClick={onClose} />
        </div>

        {/* Vehicle Info */}
        <div style={styles.vehicleInfo}>
          <span style={styles.vehicleIcon}>
            {vehicle?.type === 'Mini' ? 'üöó' : 
             vehicle?.type === 'Sedan' ? 'üöô' : 
             vehicle?.type === 'SUV' ? 'üöê' : 'üõ∫'}
          </span>
          <div>
            <h3>{vehicle?.type || 'Mini'} ‚Ä¢ {driver?.name || 'Driver'}</h3>
            <p style={styles.eta}>Arriving in {vehicle?.eta || '2-3'} minutes</p>
            <p style={styles.vehicleNumber}>{driver?.vehicleNumber || 'RJ14 XX 1234'}</p>
          </div>
          <span style={styles.fare}>‚Çπ{fare}</span>
        </div>

        {/* Trip Details */}
        <div style={styles.tripDetails}>
          <p><strong>üìç From:</strong> {pickupLocation?.address || 'Pickup location'}</p>
          <p><strong>üèÅ To:</strong> {dropLocation?.address || 'Drop location'}</p>
          <p><strong>üìè Distance:</strong> {distance} km ‚Ä¢ {duration} mins</p>
        </div>

        {/* Form */}
        <div style={styles.form}>
          <div style={styles.inputGroup}>
            <FaUser style={styles.inputIcon} />
            <input
              style={styles.input}
              placeholder="Your Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>

          <div style={styles.inputGroup}>
            <FaPhone style={styles.inputIcon} />
            <input
              style={styles.input}
              placeholder="Phone Number"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
            />
          </div>

          {/* Payment Options */}
          <div style={styles.paymentSection}>
            <h4>Payment Method</h4>
            <div style={styles.paymentOptions}>
              <button 
                style={{
                  ...styles.paymentButton,
                  ...(paymentMethod === 'cash' && styles.paymentButtonActive)
                }}
                onClick={() => setPaymentMethod('cash')}
              >
                üíµ Cash
              </button>
              <button 
                style={{
                  ...styles.paymentButton,
                  ...(paymentMethod === 'card' && styles.paymentButtonActive)
                }}
                onClick={() => setPaymentMethod('card')}
              >
                üí≥ Card
              </button>
              <button 
                style={{
                  ...styles.paymentButton,
                  ...(paymentMethod === 'upi' && styles.paymentButtonActive)
                }}
                onClick={() => setPaymentMethod('upi')}
              >
                üì± UPI
              </button>
            </div>
          </div>

          {/* Confirm Button */}
          <button 
            style={{
              ...styles.confirmButton,
              background: loading ? '#999' : '#4CAF50'
            }}
            onClick={handleConfirm}
            disabled={!name || !phone || loading}
          >
            {loading ? 'Sending Request...' : 'Confirm & Request Ride'}
          </button>
        </div>
      </div>
    </div>
  );
}

const styles = {
  overlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    background: 'rgba(0,0,0,0.5)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    zIndex: 2000,
    backdropFilter: 'blur(5px)'
  },
  modal: {
    background: 'white',
    borderRadius: '25px',
    width: '90%',
    maxWidth: '400px',
    padding: '20px',
    boxShadow: '0 20px 60px rgba(0,0,0,0.3)'
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '20px'
  },
  title: {
    margin: 0,
    fontSize: '1.5rem',
    color: '#333'
  },
  closeIcon: {
    fontSize: '1.5rem',
    cursor: 'pointer',
    color: '#999'
  },
  vehicleInfo: {
    display: 'flex',
    alignItems: 'center',
    background: '#f8f9fa',
    padding: '15px',
    borderRadius: '15px',
    marginBottom: '15px'
  },
  vehicleIcon: {
    fontSize: '2.5rem',
    marginRight: '15px'
  },
  eta: {
    margin: '2px 0',
    color: '#666',
    fontSize: '0.9rem'
  },
  vehicleNumber: {
    margin: '2px 0',
    color: '#999',
    fontSize: '0.8rem'
  },
  fare: {
    marginLeft: 'auto',
    fontSize: '1.5rem',
    fontWeight: 'bold',
    color: '#4CAF50'
  },
  tripDetails: {
    background: '#f0f0f0',
    padding: '12px',
    borderRadius: '10px',
    marginBottom: '20px',
    fontSize: '0.9rem'
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '15px'
  },
  inputGroup: {
    position: 'relative'
  },
  inputIcon: {
    position: 'absolute',
    left: '12px',
    top: '50%',
    transform: 'translateY(-50%)',
    color: '#999'
  },
  input: {
    width: '100%',
    padding: '15px 15px 15px 40px',
    border: '1px solid #ddd',
    borderRadius: '10px',
    fontSize: '1rem',
    boxSizing: 'border-box'
  },
  paymentSection: {
    marginTop: '10px'
  },
  paymentOptions: {
    display: 'flex',
    gap: '10px',
    marginTop: '10px'
  },
  paymentButton: {
    flex: 1,
    padding: '12px',
    border: '1px solid #ddd',
    background: 'white',
    borderRadius: '10px',
    cursor: 'pointer',
    transition: 'all 0.2s'
  },
  paymentButtonActive: {
    background: '#4CAF50',
    color: 'white',
    borderColor: '#4CAF50'
  },
  confirmButton: {
    padding: '15px',
    background: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '50px',
    fontSize: '1.2rem',
    fontWeight: 'bold',
    cursor: 'pointer',
    marginTop: '20px'
  }
};

export default Book;
