import React, { useState, useEffect } from "react";
import {
  MapContainer,
  TileLayer,
  Marker,
  useMapEvents,
  useMap
} from "react-leaflet";

import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "leaflet-routing-machine";


// marker fix
delete L.Icon.Default.prototype._getIconUrl;

L.Icon.Default.mergeOptions({

  iconRetinaUrl:
    "https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png",

  iconUrl:
    "https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon.png",

  shadowUrl:
    "https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png",

});


function Routing({ pickup, drop, setDistance, setFare }) {

  const map = useMap();

  useEffect(() => {

    if (!pickup || !drop) return;

    const control = L.Routing.control({

  router: L.Routing.osrmv1({

    serviceUrl:
      "https://routing.openstreetmap.de/routed-car/route/v1"

  }),

  waypoints: [

    L.latLng(pickup[0], pickup[1]),
    L.latLng(drop[0], drop[1])

  ],

  routeWhileDragging: false,

  addWaypoints: false,

  draggableWaypoints: false,

  createMarker: () => null

}).addTo(map);



    control.on("routesfound", function (e) {

      const route = e.routes[0];

      const distanceKm = route.summary.totalDistance / 1000;

      const fare = distanceKm * 10;

      console.log("Distance:", distanceKm);

      console.log("Fare:", fare);

      setDistance(distanceKm.toFixed(2));

      setFare(fare.toFixed(0));

    });



    return () => {

      map.removeControl(control);

    };

  }, [pickup, drop, map, setDistance, setFare]);



  return null;

}



function Map() {

  const [pickup, setPickup] = useState(null);

  const [drop, setDrop] = useState(null);

  const [distance, setDistance] = useState(0);

  const [fare, setFare] = useState(0);



  function ClickHandler() {

    useMapEvents({

      click(e) {

        if (!pickup)

          setPickup([e.latlng.lat, e.latlng.lng]);

        else if (!drop)

          setDrop([e.latlng.lat, e.latlng.lng]);

      }

    });

    return null;
  }



  return (

    <div>

      <MapContainer
        center={[28.6139, 77.2090]}
        zoom={13}
        style={{ height: "500px" }}
      >

        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />

        <ClickHandler />

        {pickup && <Marker position={pickup} />}

        {drop && <Marker position={drop} />}

        <Routing
          pickup={pickup}
          drop={drop}
          setDistance={setDistance}
          setFare={setFare}
        />

      </MapContainer>



      {/* SHOW DISTANCE AND FARE */}

      {distance > 0 && (

        <div style={{

          padding: "10px",
          background: "black",
          color: "white"

        }}>

          Distance: {distance} km

          <br />

          Fare: â‚¹{fare}

        </div>

      )}

    </div>

  );

}

export default Map;
