import React, { useState, useEffect } from 'react';
import { FaUser, FaStar, FaClock, FaMapMarkerAlt } from "react-icons/fa";
import axios from 'axios';
import { io } from 'socket.io-client';

function Driver({ fare, onBook, pickupLocation, dropLocation, distance, duration }) {
  const [availableDrivers, setAvailableDrivers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [socket, setSocket] = useState(null);
  const [error, setError] = useState(null);
  
  // User side states
  const [searchingForDriver, setSearchingForDriver] = useState(false);
  const [rideStatus, setRideStatus] = useState(null);
  const [assignedDriver, setAssignedDriver] = useState(null);

  // Socket listeners for ride updates
  useEffect(() => {
    if (!socket) return;

    socket.on('ride-accepted', (data) => {
      console.log("âœ… Ride accepted by driver:", data);
      setSearchingForDriver(false);
      setRideStatus('accepted');
      setAssignedDriver(data.driverId);
    });

    socket.on('ride-rejected', (data) => {
      console.log("âŒ Ride rejected:", data);
      setSearchingForDriver(false);
      alert('Driver rejected your ride. Finding another driver...');
      requestRide();
    });

    return () => {
      socket.off('ride-accepted');
      socket.off('ride-rejected');
    };
  }, [socket]);

  // Request ride function
  const requestRide = async () => {
    if (!pickupLocation || !dropLocation) {
      alert('Please select pickup and drop locations first');
      return;
    }

    setSearchingForDriver(true);
    
    try {
      const res = await axios.post('http://localhost:5000/api/rides/request', {
        userId: localStorage.getItem('userId') || 'user_' + Date.now(),
        pickupLocation: {
          lat: pickupLocation.lat,
          lng: pickupLocation.lng,
          address: pickupLocation.address || 'Pickup location'
        },
        dropLocation: {
          lat: dropLocation.lat,
          lng: dropLocation.lng,
          address: dropLocation.address || 'Drop location'
        },
        fare: fare,
        distance: distance,
        duration: duration
      });
      
      console.log("âœ… Ride requested:", res.data);
      localStorage.setItem('currentRideId', res.data.ride._id);
      
    } catch (error) {
      console.error("âŒ Error requesting ride:", error);
      setSearchingForDriver(false);
      alert('Failed to request ride. Please try again.');
    }
  };

  // Cancel ride search
  const cancelSearch = () => {
    setSearchingForDriver(false);
    setRideStatus(null);
  };

  // âœ… FIX: Connect to socket and load drivers
  useEffect(() => {
    // Connect to socket
    const newSocket = io('http://localhost:5000', {
      transports: ['websocket'],
      reconnection: true
    });
    setSocket(newSocket);

    newSocket.on('connect', () => {
      console.log('âœ… Connected to server');
      // Load drivers when connected
      loadAvailableDrivers();
    });

    // Listen for new drivers
    newSocket.on('driver-available', (data) => {
      console.log("ğŸ“¢ New driver available:", data);
      
      const newDriver = {
        _id: data.driverId || data._id,
        id: data.driverId || data._id,
        name: data.name || 'Driver',
        vehicleType: data.vehicleType || 'Mini',
        vehicleNumber: data.vehicleNumber || 'RJ14XX1234',
        currentLocation: data.location || { lat: 0, lng: 0 },
        isAvailable: true,
        rating: data.rating || 4.9
      };
      
      setAvailableDrivers(prev => {
        const exists = prev.some(d => d._id === newDriver._id);
        if (!exists) {
          console.log("â• Adding new driver to list:", newDriver);
          return [...prev, newDriver];
        }
        return prev;
      });
    });

    // Listen for drivers going offline
    newSocket.on('driver-unavailable', (data) => {
      console.log("ğŸ“¢ Driver offline:", data);
      setAvailableDrivers(prev => 
        prev.filter(d => d._id !== data.driverId && d.id !== data.driverId)
      );
    });

    return () => {
      newSocket.close();
    };
  }, []); // Empty dependency array - run once

  // âœ… FIX: Load drivers when pickupLocation changes
  useEffect(() => {
    if (pickupLocation) {
      console.log("ğŸ“ Pickup location changed, reloading nearby drivers:", pickupLocation);
      loadAvailableDrivers();
    } else {
      // Even without pickup, load all drivers
      loadAvailableDrivers();
    }
  }, [pickupLocation]);

  // âœ… FIX: loadAvailableDrivers function
  const loadAvailableDrivers = async () => {
    try {
      setLoading(true);
      setError(null);
      
      console.log("ğŸ” Loading available drivers...");
      
      // First, check all drivers via debug endpoint
      try {
        const debugRes = await axios.get('http://localhost:5000/api/debug/drivers');
        console.log("ğŸ“Š All drivers in DB:", debugRes.data);
      } catch (debugErr) {
        console.log("Debug endpoint not available");
      }
      
      // Get available drivers
      const params = {};
      if (pickupLocation) {
        params.lat = pickupLocation.lat;
        params.lng = pickupLocation.lng;
        params.radius = 500; // Increased radius
      }
      
      const res = await axios.get('http://localhost:5000/api/drivers/available', { params });
      console.log("ğŸ“¦ Drivers response:", res.data);

      if (res.data.success) {
        if (res.data.drivers && res.data.drivers.length > 0) {
          console.log(`âœ… Found ${res.data.drivers.length} drivers`);
          setAvailableDrivers(res.data.drivers);
        } else {
          console.log("âš ï¸ No drivers found in API response");
          setAvailableDrivers([]); // Clear but don't show error
        }
      }
    } catch (error) {
      console.error('âŒ Error loading drivers:', error);
      setError('Failed to load drivers');
    } finally {
      setLoading(false);
    }
  };

  // Calculate distance (mock for now)
  const getDriverDistance = () => {
    return Math.floor(Math.random() * 5) + 1;
  };

  // Calculate fare
  const getDriverFare = (driverType) => {
    const multipliers = {
      'Mini': 1,
      'Sedan': 1.5,
      'SUV': 2,
      'Auto': 0.8
    };
    return Math.round(fare * (multipliers[driverType] || 1));
  };

  // Manual refresh
  const handleRefresh = () => {
    loadAvailableDrivers();
  };

  console.log("ğŸ‘€ Current available drivers state:", availableDrivers);

  // Show searching for driver screen
  if (searchingForDriver) {
    return (
      <div style={styles.searchingContainer}>
        <div style={styles.loadingSpinner}></div>
        <h3>ğŸ” Searching for nearby drivers...</h3>
