        <div style={styles.tripDetails}>
          <p>ğŸ“ From: {pickupLocation?.address || 'Pickup'}</p>
          <p>ğŸ To: {dropLocation?.address || 'Drop'}</p>
          <p>ğŸ’° Fare: â‚¹{fare}</p>
          <p>ğŸ“ Distance: {distance} km</p>
        </div>
        <button onClick={cancelSearch} style={styles.cancelBtn}>
          Cancel Search
        </button>
      </div>
    );
  }

  // Show driver assigned screen
  if (rideStatus === 'accepted') {
    return (
      <div style={styles.assignedContainer}>
        <div style={styles.successIcon}>âœ…</div>
        <h3>Driver Assigned!</h3>
        <p>Your driver is on the way</p>
        <div style={styles.driverInfo}>
          <div style={styles.driverCard}>
            <FaUser size={40} />
            <div>
              <h4>Driver {assignedDriver}</h4>
              <p>âš¡ 5 min away</p>
              <p>ğŸš— Vehicle: Swift (RJ14 XX 1234)</p>
            </div>
          </div>
          <div style={styles.tripDetails}>
            <p>ğŸ“ Pickup: {pickupLocation?.address || 'Pickup location'}</p>
            <p>ğŸ Drop: {dropLocation?.address || 'Drop location'}</p>
            <p>ğŸ’° Fare: â‚¹{fare}</p>
          </div>
        </div>
        <button style={styles.trackBtn}>Track Driver</button>
      </div>
    );
  }

  // Loading state
  if (loading) {
    return (
      <div style={styles.loadingContainer}>
        <div style={styles.loadingSpinner}></div>
        <p>Finding nearby drivers...</p>
        <button onClick={handleRefresh} style={styles.refreshBtn}>
          ğŸ”„ Refresh
        </button>
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div style={styles.noDriversContainer}>
        <div style={styles.noDriversIcon}>âš ï¸</div>
        <h3>Error</h3>
        <p>{error}</p>
        <button style={styles.retryBtn} onClick={handleRefresh}>
          ğŸ”„ Try Again
        </button>
      </div>
    );
  }

  // No drivers state
  if (availableDrivers.length === 0) {
    return (
      <div style={styles.noDriversContainer}>
        <div style={styles.noDriversIcon}>ğŸš«</div>
        <h3>No Drivers Available</h3>
        <p>No drivers are online right now</p>
        <p style={{ fontSize: '0.8rem', color: '#999' }}>
          Socket: {socket?.connected ? 'âœ… Connected' : 'âŒ Disconnected'}
        </p>
        <button style={styles.retryBtn} onClick={handleRefresh}>
          ğŸ”„ Refresh
        </button>
        {pickupLocation && dropLocation && (
          <button 
            style={{...styles.retryBtn, background: '#ff9800', marginTop: '10px'}}
            onClick={requestRide}
          >
            ğŸš– Request Ride Anyway
          </button>
        )}
      </div>
    );
  }

  // Main render with drivers list
  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h3>{availableDrivers.length} Driver{availableDrivers.length > 1 ? 's' : ''} Available</h3>
        <span style={styles.liveBadge}>ğŸ”´ LIVE</span>
      </div>
      
      <div style={styles.driverList}>
        {availableDrivers.map((driver) => (
          <div key={driver._id} style={styles.card}>
            <div style={styles.driverIcon}>
              <span style={{ fontSize: '2rem' }}>
                {driver.vehicleType === 'Mini' ? 'ğŸš—' : 
                 driver.vehicleType === 'Sedan' ? 'ğŸš™' : 
                 driver.vehicleType === 'SUV' ? 'ğŸš' : 'ğŸ›º'}
              </span>
            </div>
            
            <div style={styles.driverDetails}>
              <div style={styles.driverHeader}>
                <div>
                  <h4>{driver.name}</h4>
                  <p style={styles.vehicleType}>{driver.vehicleType}</p>
                </div>
                <div style={styles.rating}>
                  <FaStar color="#FFD700" />
                  <span>{driver.rating || 4.9}</span>
                </div>
              </div>
              
              <div style={styles.driverInfo}>
                <span style={styles.infoItem}>
                  <FaMapMarkerAlt /> {getDriverDistance()} min away
                </span>
                <span style={styles.infoItem}>
                  <FaClock /> {driver.vehicleNumber}
                </span>
              </div>
              
              <div style={styles.fareSection}>
                <div>
                  <span style={styles.fareLabel}>Fare</span>
                  <span style={styles.fare}>â‚¹{getDriverFare(driver.vehicleType)}</span>
                </div>
                <button 
                  style={styles.bookBtn}
                  onClick={() => {
                    console.log("ğŸ“¢ Book button clicked with locations:", {
                      pickup: pickupLocation,
                      drop: dropLocation,
                      distance,
                      duration
                    });
                    
                    onBook({
                      driver,
                      pickupLocation,
                      dropLocation,
                      distance,
                      duration,
                      fare: getDriverFare(driver.vehicleType)
                    });
                  }}
                >
                  Book Now
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Debug info */}
      <div style={styles.debugInfo}>
        <p>ğŸ“ Pickup: {pickupLocation ? 'Set' : 'Not set'}</p>
        <p>ğŸ”Œ Socket: {socket?.connected ? 'Connected' : 'Disconnected'}</p>
        <button onClick={handleRefresh} style={styles.refreshBtn}>
          ğŸ”„ Manual Refresh
        </button>
      </div>
    </div>
  );
}

