import React, { useState, useEffect } from 'react';
import { FaUser, FaStar, FaClock, FaMapMarkerAlt } from "react-icons/fa";
import axios from 'axios';
import { io } from 'socket.io-client';

function Driver({ fare, onBook, pickupLocation }) {
  const [availableDrivers, setAvailableDrivers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [socket, setSocket] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Connect to socket for real-time updates
    const newSocket = io('http://localhost:5000');
    setSocket(newSocket);

    // Load available drivers
    if (pickupLocation) {
      loadAvailableDrivers();
    }

    // Listen for driver availability updates
    newSocket.on('driver-available', (data) => {
      console.log("Driver available:", data);
      // Check if driver already exists
      setAvailableDrivers(prev => {
        const exists = prev.some(d => d._id === data.driverId);
        if (!exists) {
          return [...prev, data];
        }
        return prev;
      });
    });

    newSocket.on('driver-unavailable', (data) => {
      console.log("Driver unavailable:", data);
      setAvailableDrivers(prev => prev.filter(d => d._id !== data.driverId));
    });

    newSocket.on('driver-status-update', (data) => {
      console.log("Driver status update:", data);
      setAvailableDrivers(prev => 
        prev.map(d => 
          d._id === data.driverId 
            ? { ...d, ...data }
            : d
        )
      );
    });

    return () => {
      newSocket.close();
    };
  }, [pickupLocation]);

  const loadAvailableDrivers = async () => {
    if (!pickupLocation) {
      setLoading(false);
      return;
    }

    try {
      setLoading(true);
      setError(null);
      
      console.log("Fetching drivers near:", pickupLocation);
      
      const res = await axios.get('http://localhost:5000/api/drivers/available', {
        params: {
          lat: pickupLocation.lat,
          lng: pickupLocation.lng,
          radius: 50 // Increased radius to 50km
        }
      });

      console.log("Available drivers response:", res.data);

      if (res.data.success) {
        setAvailableDrivers(res.data.drivers);
      }
    } catch (error) {
      console.error('Error loading drivers:', error);
      setError('Failed to load drivers. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Calculate distance (mock function - in real app, calculate actual distance)
  const getDriverDistance = () => {
    return Math.floor(Math.random() * 5) + 1; // Random 1-5 km
  };

  // Calculate fare based on driver type
  const getDriverFare = (driverType) => {
    const multipliers = {
      'Mini': 1,
      'Sedan': 1.5,
      'SUV': 2,
      'Auto': 0.8
    };
    return Math.round(fare * (multipliers[driverType] || 1));
  };

  if (loading) {
    return (
      <div style={styles.loadingContainer}>
        <div style={styles.loadingSpinner}></div>
        <p>Finding nearby drivers...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div style={styles.noDriversContainer}>
        <div style={styles.noDriversIcon}>‚ö†Ô∏è</div>
        <h3 style={styles.noDriversTitle}>Error</h3>
        <p style={styles.noDriversText}>{error}</p>
        <button 
          style={styles.retryBtn}
          onClick={loadAvailableDrivers}
        >
          üîÑ Try Again
        </button>
      </div>
    );
  }

  if (availableDrivers.length === 0) {
    return (
      <div style={styles.noDriversContainer}>
        <div style={styles.noDriversIcon}>üö´</div>
        <h3 style={styles.noDriversTitle}>No Cabs Available</h3>
        <p style={styles.noDriversText}>
          Sorry, no drivers are available near your location right now.
          Please try again in a few minutes.
        </p>
        <button 
          style={styles.retryBtn}
          onClick={loadAvailableDrivers}
        >
          üîÑ Check Again
        </button>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h3 style={styles.title}>
          {availableDrivers.length} Driver{availableDrivers.length > 1 ? 's' : ''} Available
        </h3>
        <span style={styles.liveBadge}>üî¥ LIVE</span>
      </div>
      
      <div style={styles.driverList}>
        {availableDrivers.map((driver) => (
          <div key={driver._id} style={styles.card}>
            <div style={styles.driverIcon}>
              <span style={{ fontSize: '2rem' }}>
                {driver.vehicleType === 'Mini' ? 'üöó' : 
                 driver.vehicleType === 'Sedan' ? 'üöô' : 
                 driver.vehicleType === 'SUV' ? 'üöê' : 'üõ∫'}
              </span>
            </div>
            
            <div style={styles.driverDetails}>
              <div style={styles.driverHeader}>
                <div>
                  <h4 style={styles.driverName}>{driver.name}</h4>
                  <p style={styles.vehicleType}>{driver.vehicleType}</p>
                </div>
                <div style={styles.rating}>
                  <FaStar style={{ color: '#FFD700' }} />
                  <span>4.9</span>
                </div>
              </div>
              
              <div style={styles.driverInfo}>
                <span style={styles.infoItem}>
                  <FaMapMarkerAlt /> {getDriverDistance()} min away
                </span>
                <span style={styles.infoItem}>
                  <FaClock /> {driver.vehicleNumber}
                </span>
              </div>
              
              <div style={styles.fareSection}>
                <div>
                  <span style={styles.fareLabel}>Estimated fare</span>
                  <span style={styles.fare}>
                    ‚Çπ{getDriverFare(driver.vehicleType)}
                  </span>
                </div>
                <button 
                  style={styles.bookBtn}
                  onClick={() => onBook({
                    ...driver,
                    fare: getDriverFare(driver.vehicleType)
                  })}
                >
                  Book Now
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Real-time info */}
      <div style={styles.realTimeInfo}>
        <p>üìç Drivers are updating their location in real-time</p>
        <button 
          onClick={loadAvailableDrivers}
          style={styles.refreshBtn}
        >
          üîÑ Refresh
        </button>
      </div>
    </div>
  );
}

