import React, { useState, useEffect } from 'react';
import { FaUser, FaStar, FaClock, FaMapMarkerAlt } from "react-icons/fa";
import axios from 'axios';
import { io } from 'socket.io-client';

function Driver({ fare, onBook, pickupLocation }) {
  const [availableDrivers, setAvailableDrivers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    // Connect to socket for real-time updates
    const newSocket = io('http://localhost:5000');
    setSocket(newSocket);

    // Load available drivers
    loadAvailableDrivers();

    // Listen for driver availability updates
    newSocket.on('driver-available', (data) => {
      setAvailableDrivers(prev => [...prev, data]);
    });

    newSocket.on('driver-unavailable', (data) => {
      setAvailableDrivers(prev => prev.filter(d => d.driverId !== data.driverId));
    });

    newSocket.on('driver-location-changed', (data) => {
      setAvailableDrivers(prev => 
        prev.map(d => 
          d.driverId === data.driverId 
            ? { ...d, location: data.location }
            : d
        )
      );
    });

    return () => newSocket.close();
  }, []);

  const loadAvailableDrivers = async () => {
    if (!pickupLocation) return;

    try {
      const res = await axios.get('http://localhost:5000/api/drivers/available', {
        params: {
          lat: pickupLocation.lat,
          lng: pickupLocation.lng,
          radius: 5
        }
      });

      if (res.data.success) {
        setAvailableDrivers(res.data.drivers);
      }
    } catch (error) {
      console.error('Error loading drivers:', error);
    } finally {
      setLoading(false);
    }
  };

  // Calculate fare based on driver type
  const getDriverFare = (driverType) => {
    const multipliers = {
      'Mini': 1,
      'Sedan': 1.5,
      'SUV': 2,
      'Auto': 0.8
    };
    return Math.round(fare * (multipliers[driverType] || 1));
  };

  if (loading) {
    return (
      <div style={styles.loadingContainer}>
        <div style={styles.loadingSpinner}></div>
        <p>Finding nearby drivers...</p>
      </div>
    );
  }

  if (availableDrivers.length === 0) {
    return (
      <div style={styles.noDriversContainer}>
        <div style={styles.noDriversIcon}>üö´</div>
        <h3 style={styles.noDriversTitle}>No Cabs Available</h3>
        <p style={styles.noDriversText}>
          Sorry, no drivers are available near your location right now.
          Please try again in a few minutes.
        </p>
        <button 
          style={styles.retryBtn}
          onClick={loadAvailableDrivers}
        >
          üîÑ Check Again
        </button>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h3 style={styles.title}>
          {availableDrivers.length} Driver{availableDrivers.length > 1 ? 's' : ''} Available
        </h3>
        <span style={styles.liveBadge}>üî¥ LIVE</span>
      </div>
      
      <div style={styles.driverList}>
        {availableDrivers.map((driver) => (
          <div key={driver._id} style={styles.card}>
            <div style={styles.driverIcon}>
              <span style={{ fontSize: '2rem' }}>üöñ</span>
            </div>
            
            <div style={styles.driverDetails}>
              <div style={styles.driverHeader}>
                <div>
                  <h4 style={styles.driverName}>{driver.name}</h4>
                  <p style={styles.vehicleType}>{driver.vehicleType}</p>
                </div>
                <div style={styles.rating}>
                  <FaStar style={{ color: '#FFD700' }} />
                  <span>4.9</span>
                </div>
              </div>
              
              <div style={styles.driverInfo}>
                <span style={styles.infoItem}>
                  <FaMapMarkerAlt /> 2 min away
                </span>
                <span style={styles.infoItem}>
                  <FaClock /> {driver.vehicleNumber}
                </span>
              </div>
              
              <div style={styles.fareSection}>
                <div>
                  <span style={styles.fareLabel}>Estimated fare</span>
                  <span style={styles.fare}>
                    ‚Çπ{getDriverFare(driver.vehicleType)}
                  </span>
                </div>
                <button 
                  style={styles.bookBtn}
                  onClick={() => onBook({
                    ...driver,
                    fare: getDriverFare(driver.vehicleType)
                  })}
                >
                  Book Now
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Real-time info */}
      <div style={styles.realTimeInfo}>
        <p>üìç Drivers are updating their location in real-time</p>
      </div>
    </div>
  );
}

const styles = {
  container: {
    height: '100%',
    display: 'flex',
    flexDirection: 'column',
    background: '#f8f9fa'
  },
  header: {
    padding: '20px',
    background: 'white',
    borderBottom: '1px solid #eee',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center'
  },
  title: {
    margin: 0,
    fontSize: '1.2rem'
  },
  liveBadge: {
    background: '#ff4444',
    color: 'white',
    padding: '4px 10px',
    borderRadius: '20px',
    fontSize: '0.8rem',
    animation: 'pulse 1.5s infinite'
  },
  driverList: {
    flex: 1,
    overflowY: 'auto',
    padding: '20px',
    display: 'flex',
    flexDirection: 'column',
    gap: '15px'
  },
  card: {
    display: 'flex',
    background: 'white',
    borderRadius: '15px',
    padding: '15px',
    boxShadow: '0 2px 8px rgba(0,0,0,0.05)',
    animation: 'slideIn 0.3s ease'
  },
  driverIcon: {
    width: '60px',
    height: '60px',
    background: '#e3f2fd',
    borderRadius: '50%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    marginRight: '15px'
  },
  driverDetails: {
    flex: 1
  },
  driverHeader: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'flex-start',
    marginBottom: '8px'
  },
  driverName: {
    margin: 0,
    fontSize: '1.1rem',
    fontWeight: '600'
  },
  vehicleType: {
    margin: '2px 0 0',
    fontSize: '0.85rem',
    color: '#666'
  },
  rating: {
    display: 'flex',
    alignItems: 'center',
    gap: '4px',
    background: '#f5f5f5',
    padding: '4px 8px',
    borderRadius: '20px',
    fontSize: '0.9rem'
  },
  driverInfo: {
    display: 'flex',
    gap: '15px',
    marginBottom: '12px'
  },
  infoItem: {
    display: 'flex',
    alignItems: 'center',
    gap: '5px',
    fontSize: '0.9rem',
    color: '#666'
  },
  fareSection: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    borderTop: '1px solid #eee',
    paddingTop: '12px'
  },
  fareLabel: {
    display: 'block',
    fontSize: '0.8rem',
    color: '#666'
  },
  fare: {
    fontSize: '1.3rem',
    fontWeight: '700',
    color: '#4CAF50'
  },
  bookBtn: {
    padding: '8px 20px',
    background: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '25px',
    fontWeight: '600',
    cursor: 'pointer',
    transition: 'all 0.3s',
    ':hover': {
      background: '#45a049',
      transform: 'scale(1.05)'
    }
  },
  loadingContainer: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '100%',
    padding: '40px'
  },
  loadingSpinner: {
    width: '50px',
    height: '50px',
    border: '3px solid #f3f3f3',
    borderTop: '3px solid #4CAF50',
    borderRadius: '50%',
    animation: 'spin 1s linear infinite',
    marginBottom: '20px'
  },
