import React, { useState, useEffect } from 'react';
import { FaUser, FaStar, FaClock, FaMapMarkerAlt } from "react-icons/fa";
import axios from 'axios';
import { io } from 'socket.io-client';

function Driver({ fare, onBook, pickupLocation }) {
  const [availableDrivers, setAvailableDrivers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [socket, setSocket] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Connect to socket for real-time updates
    const newSocket = io('http://localhost:5000', {
      transports: ['websocket'],
      reconnection: true
    });
    setSocket(newSocket);

    newSocket.on('connect', () => {
      console.log('‚úÖ Connected to server with socket ID:', newSocket.id);
    });

    newSocket.on('connect_error', (err) => {
      console.error('‚ùå Socket connection error:', err);
    });

    // Load available drivers immediately
    if (pickupLocation) {
      loadAvailableDrivers();
    } else {
      // Even without pickup location, try to load drivers
      loadAvailableDrivers();
    }

    // Listen for driver availability updates
    newSocket.on('driver-available', (data) => {
      console.log("üì¢ Driver available event received:", data);
      setAvailableDrivers(prev => {
        const exists = prev.some(d => d._id === data.driverId || d.id === data.driverId);
        if (!exists) {
          const newDriver = {
            _id: data.driverId || data._id,
            id: data.driverId || data._id,
            name: data.name,
            vehicleType: data.vehicleType,
            vehicleNumber: data.vehicleNumber || 'RJ14XX1234',
            currentLocation: data.location,
            isAvailable: true
          };
          console.log("‚ûï Adding new driver:", newDriver);
          return [...prev, newDriver];
        }
        return prev;
      });
    });

    newSocket.on('driver-unavailable', (data) => {
      console.log("üì¢ Driver unavailable event:", data);
      setAvailableDrivers(prev => prev.filter(d => 
        d._id !== data.driverId && d.id !== data.driverId
      ));
    });

    newSocket.on('driver-status-update', (data) => {
      console.log("üì¢ Driver status update:", data);
      setAvailableDrivers(prev => 
        prev.map(d => {
          if (d._id === data.driverId || d.id === data.driverId) {
            return {
              ...d,
              ...data,
              currentLocation: data.location || d.currentLocation
            };
          }
          return d;
        })
      );
    });

    return () => {
      console.log("üîå Disconnecting socket");
      newSocket.close();
    };
  }, []); // Empty dependency array to run once

  const loadAvailableDrivers = async () => {
    try {
      setLoading(true);
      setError(null);
      
      console.log("üîç Loading available drivers...");
      
      // First check all drivers in DB
