import React, { useState } from "react";

import { useNavigate } from "react-router-dom";


import bg from "../assets/car1.jpg";

function Home(){



const navigate = useNavigate();
const [pickup, setPickup] = useState("");
const [drop, setDrop] = useState("");

return(

<div>

{/* NAVBAR */}

<div style={styles.navbar}>

<h2 style={styles.logo}>RIDE</h2>

<div>

<button style={styles.navBtn}>
Book an  Cab
</button>

<button style={styles.navBtn}>
Free S1 Test Ride
</button>

</div>

</div>



{/* HERO */}

<div style={styles.hero}>

<img src={bg} style={styles.bg}/>


<div style={styles.searchBox}>


<div style={styles.tabs}>

<span style={styles.activeTab}>
Daily
</span>

<span>
Rental
</span>

<span>
Outstation
</span>

</div>



<div style={styles.inputs}>

<input
placeholder="Current Location"
style={styles.input}
value={pickup}
onChange={(e)=>setPickup(e.target.value)}
/>


<input
placeholder="Enter Location"
style={styles.input}
value={drop}
onChange={(e)=>setDrop(e.target.value)}
/>



<button 
style={styles.searchBtn}
onClick={() =>
navigate("/map", {
state: {
pickupLocation: pickup,
dropLocation: drop
}
})
}
>


SEARCH CABS →

</button>


</div>


</div>
{/* FOOTER */}

<div style={styles.footer}>

<div style={styles.footerContainer}>

<div>

<h3>RIDE</h3>

<p>Book cab in seconds</p>

</div>


<div>

<h4>Company</h4>

<p>About</p>

<p>Careers</p>

<p>Contact</p>

</div>


<div>

<h4>Services</h4>

<p>Daily</p>

<p>Rental</p>

<p>Outstation</p>

</div>


<div>

<h4>Support</h4>

<p>Help</p>

<p>Terms</p>

<p>Privacy</p>

</div>


</div>


<div style={styles.copy}>

© 2026 Ride App. All rights reserved.

</div>


</div>

</div>


</div>


);


}
const getCoordinates = async (place) => {

const res = await fetch(
`https://nominatim.openstreetmap.org/search?format=json&q=${place}`
);

const data = await res.json();

if(data.length > 0){

return {

lat: parseFloat(data[0].lat),

lng: parseFloat(data[0].lon)

};

}

return null;

};


export default Home;



const styles={

navbar:{

background:"black",
color:"white",
padding:"15px",
display:"flex",
justifyContent:"space-between"

},

logo:{

margin:0

},

navBtn:{

marginLeft:"10px",
padding:"10px",
border:"none"

},

hero:{

position:"relative"

},

bg:{

width:"100%",
height:"450px",
objectFit:"cover"

},

searchBox:{

position:"absolute",
top:"150px",
left:"50%",
transform:"translateX(-50%)",
background:"white",
padding:"20px",
width:"600px"

},

tabs:{

display:"flex",
gap:"20px",
marginBottom:"10px"

},

activeTab:{

borderBottom:"2px solid black"

},

inputs:{

display:"flex",
gap:"10px"

},

input:{

padding:"10px",
flex:1

},

searchBtn:{

background:"black",
color:"white",
padding:"10px"

},

footer:{

background:"#111",
color:"white",
padding:"40px 20px",
marginTop:"50px"

},

footerContainer:{

display:"flex",
justifyContent:"space-around",
flexWrap:"wrap"

},

copy:{

textAlign:"center",
marginTop:"20px",
color:"gray"

}


}
